<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$word&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">trim</span><span style="color: #007700">(</span><span style="color: #0000BB">$word</span><span style="color: #007700">);
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;If&nbsp;the&nbsp;previous&nbsp;word&nbsp;was&nbsp;an&nbsp;'is'&nbsp;'has'&nbsp;or&nbsp;'be'&nbsp;verb...
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #007700">if&nbsp;(</span><span style="color: #0000BB">$gotverb&nbsp;</span><span style="color: #007700">!=&nbsp;</span><span style="color: #DD0000">'0'</span><span style="color: #007700">)&nbsp;{&nbsp;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;If&nbsp;the&nbsp;current&nbsp;word&nbsp;is&nbsp;a&nbsp;past&nbsp;participle
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #007700">if&nbsp;(</span><span style="color: #0000BB">substr</span><span style="color: #007700">(</span><span style="color: #0000BB">$word</span><span style="color: #007700">,-</span><span style="color: #0000BB">2</span><span style="color: #007700">)&nbsp;==&nbsp;</span><span style="color: #DD0000">'ed'&nbsp;</span><span style="color: #007700">||&nbsp;</span><span style="color: #0000BB">in_array</span><span style="color: #007700">(</span><span style="color: #0000BB">$word</span><span style="color: #007700">,</span><span style="color: #0000BB">$irregular</span><span style="color: #007700">))&nbsp;{&nbsp;</span><span style="color: #0000BB">$passive</span><span style="color: #007700">[]&nbsp;=&nbsp;</span><span style="color: #0000BB">$gotverb</span><span style="color: #007700">.&nbsp;</span><span style="color: #DD0000">"&nbsp;"</span><span style="color: #007700">.&nbsp;</span><span style="color: #0000BB">$word</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$pair&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$gotverb</span><span style="color: #007700">.&nbsp;</span><span style="color: #DD0000">"&nbsp;"</span><span style="color: #007700">.&nbsp;</span><span style="color: #0000BB">$word</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
<br />&nbsp;&nbsp;&nbsp;&nbsp;}
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;Reset&nbsp;the&nbsp;checker
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$gotverb&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'0'</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #FF8000">//&nbsp;Search&nbsp;for&nbsp;'is'&nbsp;'has'&nbsp;and&nbsp;'be'&nbsp;verbs
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #007700">if&nbsp;(</span><span style="color: #0000BB">in_array</span><span style="color: #007700">(</span><span style="color: #0000BB">$word</span><span style="color: #007700">,</span><span style="color: #0000BB">$verbs</span><span style="color: #007700">))&nbsp;{&nbsp;
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$gotverb&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$word</span><span style="color: #007700">;}
<br />&nbsp;&nbsp;&nbsp;&nbsp;}
<br /></span><span style="color: #0000BB">$passivecount&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">count</span><span style="color: #007700">(</span><span style="color: #0000BB">$passive</span><span style="color: #007700">);
<br />
<br /></span><span style="color: #FF8000">//&nbsp;Spelling&nbsp;check
<br />&nbsp;</span><span style="color: #0000BB">$errors&nbsp;</span><span style="color: #007700">=&nbsp;array();
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$corrects&nbsp;</span><span style="color: #007700">=&nbsp;array();
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$associated&nbsp;</span><span style="color: #007700">=&nbsp;array();
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$sql&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">"&nbsp;SELECT&nbsp;id,error,correct&nbsp;FROM&nbsp;misspellings&nbsp;"</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$result&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">mysql_query</span><span style="color: #007700">(</span><span style="color: #0000BB">$sql</span><span style="color: #007700">)&nbsp;&nbsp;&nbsp;&nbsp;or&nbsp;die(</span><span style="color: #0000BB">mysql_error</span><span style="color: #007700">());
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(</span><span style="color: #0000BB">$row&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">mysql_fetch_array</span><span style="color: #007700">(</span><span style="color: #0000BB">$result</span><span style="color: #007700">))&nbsp;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">extract</span><span style="color: #007700">(</span><span style="color: #0000BB">$row</span><span style="color: #007700">);&nbsp;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$errors</span><span style="color: #007700">[]&nbsp;=&nbsp;</span><span style="color: #0000BB">$error</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$corrects</span><span style="color: #007700">[]&nbsp;=&nbsp;</span><span style="color: #0000BB">$correct</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$associated</span><span style="color: #007700">[</span><span style="color: #0000BB">$error</span><span style="color: #007700">]&nbsp;=&nbsp;</span><span style="color: #0000BB">$correct</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
<br />&nbsp;</span><span style="color: #0000BB">$count&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br />&nbsp;</span><span style="color: #0000BB">$spellingmost&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;(</span><span style="color: #0000BB">$errors&nbsp;</span><span style="color: #007700">as&nbsp;</span><span style="color: #0000BB">$check</span><span style="color: #007700">)&nbsp;{
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$added&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #DD0000">'&nbsp;'</span><span style="color: #007700">.&nbsp;</span><span style="color: #0000BB">$check</span><span style="color: #007700">.&nbsp;</span><span style="color: #DD0000">'&nbsp;'</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$pos&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">strpos</span><span style="color: #007700">(</span><span style="color: #0000BB">$clean</span><span style="color: #007700">,</span><span style="color: #0000BB">$added</span><span style="color: #007700">);
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if((</span><span style="color: #0000BB">$pos&nbsp;</span><span style="color: #007700">!==&nbsp;</span><span style="color: #0000BB">false</span><span style="color: #007700">))&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$count&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$count</span><span style="color: #007700">+</span><span style="color: #0000BB">substr_count</span><span style="color: #007700">(</span><span style="color: #0000BB">$clean</span><span style="color: #007700">,</span><span style="color: #0000BB">$added</span><span style="color: #007700">);
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(</span><span style="color: #0000BB">substr_count</span><span style="color: #007700">(</span><span style="color: #0000BB">$clean</span><span style="color: #007700">,</span><span style="color: #0000BB">$check</span><span style="color: #007700">)&nbsp;&gt;&nbsp;</span><span style="color: #0000BB">$spellingmost</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$spellingmost&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">substr_count</span><span style="color: #007700">(</span><span style="color: #0000BB">$clean</span><span style="color: #007700">,</span><span style="color: #0000BB">$added</span><span style="color: #007700">);&nbsp;</span><span style="color: #0000BB">$commonspelling&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$check</span><span style="color: #007700">;&nbsp;}
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}
<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;
<br /></span><span style="color: #0000BB">$spellinggrammar&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$grammarcount</span><span style="color: #007700">;
<br />
<br /></span><span style="color: #FF8000">//&nbsp;SCORING
<br />//&nbsp;Assign&nbsp;the&nbsp;allowed&nbsp;threshold&nbsp;for&nbsp;scoring
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$allowedpassive&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">10</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$allowedwordiness&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">5</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$allowedgrammar&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$allowedtransitions&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">25</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$allowedandbutor&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">5</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: #0000BB">$allowedvariety&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">50</span><span style="color: #007700">;
<br />&nbsp;&nbsp;&nbsp;&nbsp;}
<br />
<br /></span><span style="color: #FF8000">//&nbsp;Assumes&nbsp;no&nbsp;thresholds&nbsp;are&nbsp;passed,&nbsp;then&nbsp;checks&nbsp;if&nbsp;any&nbsp;are
<br /></span><span style="color: #0000BB">$passivescore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br /></span><span style="color: #0000BB">$wordyscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br /></span><span style="color: #0000BB">$grammarscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br /></span><span style="color: #0000BB">$transitionsscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br /></span><span style="color: #0000BB">$andbutorscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br /></span><span style="color: #0000BB">$varietyscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;
<br />
<br />if((</span><span style="color: #0000BB">count</span><span style="color: #007700">(</span><span style="color: #0000BB">$passive</span><span style="color: #007700">)/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">)&nbsp;&gt;&nbsp;</span><span style="color: #0000BB">$allowedpassive</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$passivescore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">number_format</span><span style="color: #007700">(</span><span style="color: #0000BB">count</span><span style="color: #007700">(</span><span style="color: #0000BB">$passive</span><span style="color: #007700">)/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">)-</span><span style="color: #0000BB">$allowedpassive</span><span style="color: #007700">;&nbsp;}
<br />if((</span><span style="color: #0000BB">$spellinggrammar</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">)&nbsp;&gt;&nbsp;</span><span style="color: #0000BB">$allowedgrammar</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$grammarscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">number_format</span><span style="color: #007700">(</span><span style="color: #0000BB">$spellinggrammar</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">200</span><span style="color: #007700">)-</span><span style="color: #0000BB">$allowedgrammar</span><span style="color: #007700">;&nbsp;}
<br />if((</span><span style="color: #0000BB">$wordycount</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">)&nbsp;&gt;&nbsp;</span><span style="color: #0000BB">$allowedwordiness</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$wordyscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">number_format</span><span style="color: #007700">(</span><span style="color: #0000BB">$wordycount</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">150</span><span style="color: #007700">)-</span><span style="color: #0000BB">$allowedwordiness</span><span style="color: #007700">;&nbsp;}
<br />if((</span><span style="color: #0000BB">$transitionscount</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">)&nbsp;&lt;&nbsp;</span><span style="color: #0000BB">$allowedtransitions</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$transitionsscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$allowedtransitions</span><span style="color: #007700">-</span><span style="color: #0000BB">number_format</span><span style="color: #007700">(</span><span style="color: #0000BB">$transitionscount</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">);&nbsp;}
<br />if((</span><span style="color: #0000BB">$andbutorcount</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">)&nbsp;&gt;&nbsp;</span><span style="color: #0000BB">$allowedandbutor</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$andbutorscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">number_format</span><span style="color: #007700">(</span><span style="color: #0000BB">$andbutorcount</span><span style="color: #007700">/</span><span style="color: #0000BB">$sentencecount</span><span style="color: #007700">*</span><span style="color: #0000BB">100</span><span style="color: #007700">)-</span><span style="color: #0000BB">$allowedandbutor</span><span style="color: #007700">;&nbsp;}
<br />if(</span><span style="color: #0000BB">$sdpercent&nbsp;</span><span style="color: #007700">&lt;&nbsp;</span><span style="color: #0000BB">$allowedvariety</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$varietyscore&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$allowedvariety</span><span style="color: #007700">-</span><span style="color: #0000BB">$sdpercent</span><span style="color: #007700">;&nbsp;}
<br />
<br />
<br /></span><span style="color: #0000BB">$problems&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">$runoncount</span><span style="color: #007700">+</span><span style="color: #0000BB">$wordycount</span><span style="color: #007700">+</span><span style="color: #0000BB">$andbutorcount</span><span style="color: #007700">+</span><span style="color: #0000BB">$passivecount</span><span style="color: #007700">+</span><span style="color: #0000BB">$spellinggrammar</span><span style="color: #007700">;
<br />
<br /></span><span style="color: #0000BB">$score&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">number_format</span><span style="color: #007700">(</span><span style="color: #0000BB">100</span><span style="color: #007700">-(</span><span style="color: #0000BB">$passivescore</span><span style="color: #007700">+</span><span style="color: #0000BB">$wordyscore</span><span style="color: #007700">+</span><span style="color: #0000BB">$grammarscore</span><span style="color: #007700">+</span><span style="color: #0000BB">$transitionsscore</span><span style="color: #007700">+</span><span style="color: #0000BB">$andbutorscore</span><span style="color: #007700">+</span><span style="color: #0000BB">$varietyscore</span><span style="color: #007700">));
<br />if&nbsp;(</span><span style="color: #0000BB">$score&nbsp;</span><span style="color: #007700">&lt;&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">)&nbsp;{&nbsp;</span><span style="color: #0000BB">$score&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #0000BB">0</span><span style="color: #007700">;&nbsp;}
<br /></span><span style="color: #0000BB">?&gt;</span>
</span>
</code>
</div>
<?php
